#dim bytesWritten as %Integer = ##class(%File).GetFileSize(filename) open filename:("RLUD":/IOTABLE="RAW") use filename try { #dim compressedData as %String read compressedData#$$$MaxStringLength } catch { close filename use io throw }
close filename use io
#dim bytesRead as %Integer = $length(compressedData) if (bytesRead '= bytesWritten) { throw ##class(SystemException).%New(, , , "Compress(): " _ bytesRead _ " byte(s) read; expected: " _ bytesWritten) }
return compressedData

#dim binary as %String = ..DecompressImpl(data, "RAW")
#dim unicode as %String = ..DecompressImpl(data, "UTF8")
/*
 * If both decompression results are the same, no further compression is necessary.
 */
if (binary = unicode) {
	return unicode
}
/*
 * We test whether a compressed result is the same as input.
 * If both results match the input, we prefer the Unicode version.
 */
return $select(..Compress(unicode) = data : unicode, 1 : binary)

if (data = "") {
	set $ecode = ""
	throw ##class(SystemException).%New("<ILLEGAL VALUE>", "78", $stack($stack(-1), "PLACE"))
}

#dim io = $io

#dim filename as %String = "d-" _ $system.Util.CreateGUID() _ ".gz"
open filename:("WLUN":/IOTABLE="RAW")
use filename
write data
close filename

#dim bytesWritten as %Integer = ##class(%File).GetFileSize(filename)
if ($length(data) '= bytesWritten) {
	throw ##class(SystemException).%New(, , , "Decompress(): " _ bytesWritten _ " byte(s) written; expected: " _ $length(data))
}

open filename:("RLUD":/IOTABLE=iotable:/GZIP=9)
use filename
#dim e as SystemException
try {
	#dim decompressedData as %String
	read decompressedData#$$$MaxStringLength
} catch (e) {
	if (e.Name = "<ENDOFFILE>") {
		/*
		 * We're trying to read an empty gzip stream.
		 */
		set decompressedData = ""
	} else {
		close filename
		use io
		throw e
	}
}
close filename
use io	

return decompressedData

/*
 * 8-bit instances don't have Unicode strings.
 */
if ('$system.Version.IsUnicode()) {
	return 0
}

#dim i as %Integer
for i = 1:1:$length(s) {
	if ($ascii(s, i) > 255) {
		return 1
	}
}
return 0
